sys_variable_value.value:
//get data from custom table which ae mapping workflow get stored here.
var gr=new GlideRecord('x_aetp_vyom_automa_ae_mapping');
gr.query();
var current_request_item=current.getDisplayValue('cat_item');
var curr_num=current.number;
gs.addInfoMessage(curr_num);
var wkfl_name;
var wkfl_url;
var question_name;
var json_field;
while(gr.next())
	{
	var cat_item=gr.getDisplayValue('u_catalog_item');
	if(current_request_item==cat_item)
		{
		wkfl_name= gr.getDisplayValue('u_workflow_name');
		wkfl_url= gr.getValue('u_workflow_url');
		json_field= gr.getValue('u_json');
	}
}
//json which will pares json overrite question value into it.using compareing question
var array_data=[];
var t=JSON.parse(json_field);
for(var p=0;p<t.length;p++)
	{
	var json=t[p];
	
	for(var u in json)
		{
		var f=u;
		var f1=json[f];
		
		if(typeof(f1)=="object")
			{
			
			for(var r=0;r<json.params.length;r++)
				{
				
				
				var question_1=json.params[r].value;
				array_data.push(question_1);
			}
			break;
			
		}
	}
}
var mapData={};
	mapData.paramData =[];
	for(var i=0;i<array_data.length;i++)
		{
		var sysid_question;
		var req_number = current.number;
		var questionnn;
		var question = array_data[i];
			
			
			
			gs.addInfoMessage(question);
		//gs.addInfoMessage(question);
			gs.addInfoMessage("hiiiiii");
		var gr1 = new GlideRecord('sc_item_option_mtom');
		//gr1.addQuery(gr1.getDisplayValue('request_item'),req_number);
		gr1.query();
			gs.addInfoMessage("helllllllooooooooooo");
		while(gr1.next())
			{
			//var n=gr1.getDisplayValue('request_item');
				
				gs.addInfoMessage(sc_item_option.item_option_new);
				var e=gr1.getDisplayValue('sc_item_option');
				gs.addInfoMessage("sys ID"+e.value);
			    gs.addInfoMessage("sys ID1"+e.item_option_new);

			if(gr1.getDisplayValue('sc_item_option')==question && gr1.getDisplayValue('request_item')==req_number)
				{
				questionnn=gr1.getDisplayValue('sc_item_option');
					
				sysid_question = gr1.getValue('sc_item_option');
					
				
			}
		}
		var gr2 = new GlideRecord('sc_item_option');
		gr2.addQuery(gr2.sys_id,sysid_question);
		gr2.query();
		
			gs.addInfoMessage("friends GB");
			
		while(gr2.next()){
			if(gr2.sys_id==sysid_question){
				var Answer = gr2.getValue('value');
				gs.addInfoMessage("question="+questionnn +"answer="+Answer);
				
				
				var param = {'question':questionnn, 'answer':Answer}; // question and value stored it into th custom json
					mapData.paramData.push(param);
				}
			}
		}
		
		oldparse();
		// fetch perv json and overrite question value and stored in the custom table.
		var Final_ae_mapping_json;
		function oldparse()
		{
			var t=JSON.parse(json_field);
			for(var p=0;p<t.length;p++)
				{
				var json=t[p];
				for(var u in json)
					{
					var f=u;
					var f1=json[f];
					// 		if(f=="sourceId") {
						//         	t[p].sourceId = current.number;
						// 			gs.addInfoMessage("f1:"+f1);
						// 			gs.addInfoMessage('json:'+ JSON.stringify(t[p].sourceId));
						// 		}
						if(typeof(f1)=="object")
							{
							for(var r=0;r<json.params.length;r++)
								{
								for(var j=0; j <mapData.paramData.length;j++) {
									var question_1=json.params[r].value;
									if(question_1 == mapData.paramData[j].question){
										json.params[r].value = mapData.paramData[j].answer;
										var u1=current.number;
										json.sourceId=u1.toString();
										var gr=new GlideRecord('v_user_session');
										gr.query();
										var ur="";
										while(gr.next())
											{
											ur=gr.getValue('user');
										}
										json.userId=ur;
									}
								}
							}
							break;
							
						}
					}
				}
				Final_ae_mapping_json=JSON.stringify(t);
				gs.addInfoMessage(Final_ae_mapping_json);
			}
			// store all ae mapping data json stored into the u_vyom_automation_edge_consume_table
			//current_request_item;
			//Final_ae_mapping_json;
			//wkfl_name
			//wkfl_url
			//current.number;
			var vyom_consume_table=new GlideRecord('x_aetp_vyom_automa_ae_staging');
			vyom_consume_table.initialize();
			vyom_consume_table.u_service_catalog_number=current.number;
			vyom_consume_table.u_service_catalog_item=current_request_item;
			vyom_consume_table.u_workflow_name=wkfl_name;
			vyom_consume_table.u_workflow_url=wkfl_url;
			vyom_consume_table.u_string_full_utf8_1=Final_ae_mapping_json;
			vyom_consume_table.insert();

